<?require("../Model/Funcionario.php");if($_POST){		$FuncionarioNome = $_POST['nome_func'];		if (empty($FuncionarioNome)){			return AlertaMsg("Nome do funcionrio deve ser preenchido!");		}				$FuncionarioNascimento = $_POST['nascimento_func'];		$FuncionarioCpf = $_POST['cpf_func'];		$FuncionarioCpf = str_replace('.','',$FuncionarioCpf);		$FuncionarioCpf = str_replace('-','',$FuncionarioCpf);		$FuncionarioRg = $_POST['rg_func'];		$FuncionarioSexo = $_POST['sexo_func'];				$FuncionarioTelefone = $_POST['telefone_func'];		$FuncionarioTelefone = str_replace('(','',$FuncionarioTelefone);		$FuncionarioTelefone = str_replace(')','',$FuncionarioTelefone);		$FuncionarioTelefone = str_replace(' ','',$FuncionarioTelefone);		$FuncionarioTelefone = str_replace('-','',$FuncionarioTelefone);		$FuncionarioCelular = $_POST['celular_func'];		$FuncionarioCelular = str_replace('(','',$FuncionarioCelular);		$FuncionarioCelular = str_replace(')','',$FuncionarioCelular);		$FuncionarioCelular = str_replace(' ','',$FuncionarioCelular);		$FuncionarioCelular = str_replace('-','',$FuncionarioCelular);		$FuncionarioFuncao = $_POST['funcao'];				$funcionario = new Funcionario();		$funcionario->setNome(strtoupper($FuncionarioNome));		$funcionario->setNascimento($FuncionarioNascimento);		$funcionario->setSexo($FuncionarioSexo);		$funcionario->setCpf($FuncionarioCpf);		$funcionario->setRg(strtoupper($FuncionarioRg));		$funcionario->setTelefone($FuncionarioTelefone);		$funcionario->setCelular($FuncionarioCelular);		$funcionario->setFuncao($FuncionarioFuncao);				$FuncionarioId = $funcionario->cadFuncionario();		if($FuncionarioId){			AlertaMsg2("Funcionrio cadastrado com sucesso!");		}else{			Alerta();		}}?>